function getColor(state)
    if state == 1 then
        -- Stopped
        color = {r=1, g=0, b=0}
    elseif state == 2 or state == 4 or state == 6 then
        -- Running / Output full / Pending
        color = {r=0, g=1, b=0}
    elseif state == 3 then
        -- Missing ingredient
        color = {r=1, g=0.6, b=0}
    elseif state == 7 then
        -- Missing schematic
        color = {r=0.5, g=0, b=1}
    else
        -- No output container / Server error
        color = {r=1, g=1, b=1}
    end
    return color
end
